import { useEffect } from "react";
import axios from "axios";

function serverHealthCheck(setServerState) {
	useEffect(() => {
		const checkServerHealth = () => {
			fetch("http://localhost:5000/api/serverhealth")
				.then(() => setServerState(true))
				.catch((error) => {
					console.log("Error checking server health: " + error);
					setServerState(false);
				});
		};

		checkServerHealth();
		const intervalId = setInterval(checkServerHealth, 5000);
		return () => clearInterval(intervalId);
	}, []);
}

function checkServerOnline(setIsOnline) {
	useEffect(() => {
		window.addEventListener("online", () => setIsOnline(true));
		window.addEventListener("offline", () => setIsOnline(false));

		return () => {
			window.removeEventListener("online", () => setIsOnline(true));
			window.removeEventListener("offline", () => setIsOnline(false));
		};
	}, []);
}

async function getAllUsers(setArray, setServerState) {
	const token = localStorage.getItem("authToken");
	useEffect(() => {
		fetch("http://localhost:5000/api", {
			headers: { Authorization: `Bearer ${token}` },
		})
			.then((response) => response.json())
			.then((data) => {
				const sorted = data.sort((a, b) => {
					let x = a.Name.toLowerCase();
					let y = b.Name.toLowerCase();
					return x < y ? -1 : x > y ? 1 : 0;
				});
				setArray(sorted);
			})
			.catch((err) => {
				setServerState(false);
				console.log(err);
			});
	}, []);
}

function getOneUser(personId, setPerson, setPetArr) {
	useEffect(() => {
		fetch(`http://localhost:5000/api/${personId}`)
			.then((response) => response.json())
			.then((data) => {
				setPerson(data.user);
				setPetArr(data.pets);
			})
			.catch((err) => console.log(err));
	}, []);
}

function deleted(id) {
	fetch(`http://localhost:5000/delete/${id}`, {
		method: "DELETE",
	})
		.then((res) => {
			if (res.ok) window.location.reload();
			else if (res.status === 402)
				return response.text().then((message) => {
					alert(message);
				});
		})
		.catch((err) => {
			alert("Cannot delete user! He has pets");
			return;
		});
}

function deletePet(aid) {
	fetch(`http://localhost:5000/api/pet/${aid}`, {
		method: "DELETE",
	}).then(window.location.reload());
}

function createPerson(e, name, age, salary, password, setname, setage, setsalary, setPassword, history) {
	e.preventDefault();

	if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
		alert("invalid input");
		setname("");
		setage("");
		setsalary("");
		setPassword("");
		window.location.reload();
		return;
	}

	const newUser = { name: name, age: age, salary: salary, password: password };
	fetch("http://localhost:4000/auth", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(newUser),
	})
		.then(() => window.location.reload())
		.catch((err) => console.log(err));

	history("/home");
}

function updateUser(e, id, name, age, salary, setname, setage, setSalary, history) {
	e.preventDefault();
	if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
		alert("invalid input");
		setname("");
		setage("");
		setSalary("");
		window.location.reload();
		return;
	}

	const newData = {
		updatedUser: {
			Name: name,
			Age: parseInt(age),
			Salary: parseInt(salary),
		},
	};

	axios.put(`http://localhost:5000/api/${id}`, newData).catch((err) => console.log(err));

	history("/home");
}

function getPetDetails(aid, setPet) {
	useEffect(() => {
		fetch(`http://localhost:5000/api/pet/${aid}`)
			.then((res) => res.json())
			.then((data) => {
				setPet(data[0]);
			})
			.catch((err) => console.log(err));
	}, []);
}

function createPet(e, uid, name, history) {
	e.preventDefault();

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const newPet = { pet: { uid: uid, Name: name } };
	fetch("http://localhost:5000/api/pet", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(newPet),
	})
		.then((res) => {
			if (!res.ok) throw new Error();
		})
		.catch((err) => {
			history("/home");
			alert("User with that ID doesn't exist... ");
		});

	history(`/person/${uid}`);
}

function editPet(e, aid, uid, name, history) {
	e.preventDefault();

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const updatedPet = {
		updatedPet: { uid: parseInt(uid), Name: name },
	};
	axios.put(`http://localhost:5000/api/pet/${aid}`, updatedPet).catch((err) => {
		history("/");
		alert("User with that ID doesn't exist... ");
	});

	history(`/person/${uid}`);
}

function sendLoginData(e, name, pass, setAuthToken, history) {
	e.preventDefault();

	const login_data = { name: name, password: pass };
	fetch("http://localhost:4000/login", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(login_data),
	})
		.then(async (res) => {
			return res.json();
		})
		.then((data) => {
			setAuthToken(data.accessToken);
			localStorage.setItem("authToken", data.accessToken);
			history("/home");
		});
}

export default { serverHealthCheck, checkServerOnline, getAllUsers, getOneUser, deleted, deletePet, createPerson, updateUser, getPetDetails, createPet, editPet, sendLoginData };
