import React, { useContext, useState } from "react";
import { Button, Form } from "react-bootstrap";
import useFetch from "../hooks/useFetch";
import { AuthTokenContext } from "../App";
import { Link, useNavigate } from "react-router-dom";

function LogIn() {
	const [name, setName] = useState("");
	const [pass, setPass] = useState("");
	const [authToken, setAuthToken] = useContext(AuthTokenContext);
	const history = useNavigate();

	return (
		<div>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }}>
				<Form.Group className="mb-3" controlId="formBasicName">
					<Form.Control onChange={(e) => setName(e.target.value)} type="text" placeholder="Enter Name" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicPassword">
					<Form.Control onChange={(e) => setPass(e.target.value)} type="password" placeholder="Password" required />
				</Form.Group>

				<Button onClick={(e) => useFetch.sendLoginData(e, name, pass, setAuthToken, history)} variant="primary" type="submit">
					Submit
				</Button>

				<Link className="d-grid gap-2" to="/auth">
					<Button variant="warning" size="lg">
						Create
					</Button>
				</Link>
			</Form>
		</div>
	);
}

export default LogIn;
