import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Create from "./components/Create";
import Edit from "./components/Edit";
import Home from "./components/Home";
import Details from "./components/Details";
import MyChart from "./components/Chart";
import PetCreate from "./components/petCreate";
import PetEdit from "./components/petEdit";
import PetDetails from "./components/petDetails";

export const Context = React.createContext();

function App() {
  const [arr, setArr] = useState([]);
  const [petArr, setPetArr] = useState([]);

  const [serverState, setServerState] = useState(false);
  const [IsOnline, setIsOnline] = useState(true);

  //server health check
  useEffect(() => {
    const checkServerHealth = () => {
      fetch("http://localhost:5000/api/serverhealth")
        .then(() => setServerState(true))
        .catch((error) => {
          console.log("Error checking server health: " + error);
          setServerState(false);
        });
    };

    checkServerHealth();
    const intervalId = setInterval(checkServerHealth, 5000);
    return () => clearInterval(intervalId);
  }, []);

  //check if there is network connection
  useEffect(() => {
    window.addEventListener("online", () => setIsOnline(true));
    window.addEventListener("offline", () => setIsOnline(false));

    return () => {
      window.removeEventListener("online", () => setIsOnline(true));
      window.removeEventListener("offline", () => setIsOnline(false));
    };
  }, []);

  return !IsOnline ? (
    <div>Check your network</div>
  ) : !serverState ? (
    <div>400 Server down</div>
  ) : !arr ? (
    <div>Array loading...</div>
  ) : (
    <>
      <Context.Provider value={[arr, setArr, petArr, setPetArr]}>
        <Router>
          <Routes>
            {/* Person routes */}
            <Route path="/" element={<Home />} />
            <Route path="/create" element={<Create />} />
            <Route path="/edit" element={<Edit />} />
            <Route path="/person/:personId" element={<Details />} />
            <Route path="/chart" element={<MyChart />} />

            {/* Pet routes */}
            <Route path="/pet/:aid" element={<PetDetails />} />
            <Route path="/pet/create" element={<PetCreate />} />
            <Route path="/pet/edit" element={<PetEdit />} />
          </Routes>
        </Router>
      </Context.Provider>
    </>
  );
}

export default App;
