import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import useLocalStorage from "../hooks/useLocalStorage";

function PetCreate() {
	const [aid, setAid] = useState("");
	const [uid, setUid] = useState("");
	const [name, setName] = useState("");

	let history = useNavigate();

	const handelSubmit = (e) => {
		function editPet(e, aid, uid, name, history) {
			e.preventDefault();
			const token = localStorage.getItem("authToken");

			if (uid === "" || name === "" || parseInt(uid) < 0) {
				alert("Invalid input");
				window.location.reload();
				return;
			}

			const updatedPet = {
				updatedPet: { uid: parseInt(uid), Name: name },
			};

			const response = axios
				.put(`http://localhost:5000/api/pet/${aid}`, updatedPet, {
					headers: {
						Authorization: `Bearer ${token}`,
						"Content-Type": "application/json",
					},
				})
				.then((res) => {
					alert(res.data.message);
					history(`/person/${uid}`);
				})
				.catch((err) => {
					if (err.response) {
						alert(err.response.data.message);
					} else {
						alert("An unexpected error occurred");
					}
					history("/home");
				});
		}
	};
	useLocalStorage.getPetEditDetails(setAid, setUid, setName);

	return (
		<div style={{ padding: "20px" }}>
			<Link to={`/person/${uid}`}>
				<Button>Back</Button>
			</Link>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }}>
				<Form.Group className="mb-3" controlId="formBasicUid">
					<Form.Control value={uid} onChange={(e) => setUid(e.target.value)} type="number" placeholder="Enter Uid" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicName">
					<Form.Control value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="Enter Name" required />
				</Form.Group>

				<Button onClick={(e) => handelSubmit(e)} variant="primary" type="submit">
					Submit
				</Button>
				<Link className="d-grid gap-2" to="/">
					<Button variant="info" size="lg">
						Home
					</Button>
				</Link>
			</Form>
		</div>
	);
}

export default PetCreate;
