// Filename - components/Home.js
import array from "./array";
import React, { useEffect, useState } from "react";
import { Button, Table } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";

function Home() {
	const [myArray, setArray] = useState(array);
	// const [sortedArray, setArray] = useState([]);
	// const [currentPage, setCurrentPage] = useState(1);
	
	// const recPerPage = 3;
	// const lastIndex = currentPage * recPerPage;
	// const firstIndex = lastIndex - recPerPage;
	// const records = array.slice(firstIndex, lastIndex);
	// const nPage = Math.ceil(array.length / recPerPage);
	// const numbers = [...Array(nPage + 1).keys()].slice(1);

	let history = useNavigate();
	

	//sort the array once before rendering the component using the use effect hook without any dependencies
	useEffect(() => {

		axios.get('/')
		.then((res) => {
			console.log(res.data)
			setArray(res.data)
		})
		.catch((err) => console.log(err));

		// const sorted = array.sort(function(a, b){
		// 	let x = a.Name.toLowerCase();
		// 	let y = b.Name.toLowerCase();
		// 	if (x < y) {return -1;}
		// 	if (x > y) {return 1;}
		// 	return 0;
		//   });
		//   setArray(sorted);
	}, []);

	function setID(id, name, age, salary) {
		localStorage.setItem("id", id);
		localStorage.setItem("Name", name);
		localStorage.setItem("Age", age);
		localStorage.setItem("Salary", salary);
	}

	function deleted(id) {
		let index = array
			.map(function (e) {
				return e.id;
			})
			.indexOf(id);

		// deleting the entry with index
		array.splice(index, 1);

		history("/");
	}

	const handlePrev = () => {currentPage !== 1 && setCurrentPage(currentPage - 1)};
	const handlePChange = (n) => {setCurrentPage(n)};
	const handleNext = () => {currentPage !== nPage && setCurrentPage(currentPage + 1)};

	return (
		<div style={{ margin: "5rem" }}>
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Name</th>
						<th>Update</th>
						<th>Delete</th>
					</tr>
				</thead>
				<tbody>
					{myArray.map((item) => {
						return (
							<tr key={item.id}>
								<Link to={`/person/${item.id}`}> <td>{item.Name}</td> </Link>
								<td>
									<Link to={`/edit`}>
										<Button
											onClick={() =>
												setID(
													item.id,
													item.Name,
													item.Age,
													item.Salary,
												)
											}
											variant="info"
										>
											Update
										</Button>
									</Link>
								</td>

								{/* Using thr deleted function passing
									the id of an entry */}
								<td>
									<Button
										onClick={() =>
											deleted(item.id)
										}
										variant="danger"
									>
										Delete
									</Button>
								</td>
							</tr>
						);
					})}
				</tbody>
			</Table>
			
			{/* <nav>
				<ul className="pagination">
					<li className="page-item">
						<a href="#" className="page-link" onClick={handlePrev}>Prev</a>
					</li>
					{
						numbers.map((n, i) => (
							<li className={`page-item ${currentPage === n ? "active" : ''}`} key={i}>
								<a href="#" className="page-link" onClick={() => handlePChange(n)}>{n}</a>
							</li>
						))
					}
				
					<li className="page-item">
						<a href="#" className="page-link" onClick={handleNext}>Next</a>
					</li>
				</ul>
			</nav> */}

			<Link className="d-grid gap-2" to="/create">
				<Button variant="warning" size="lg">
					Create
				</Button>
			</Link>

			<Link to ={"/chart"}>
				<Button
				variant="contained">
					View Chart
				</Button>
			</Link>
		</div>
	);
}

export default Home;
