// Filename - components/Home.js

import React, { useEffect, useState } from "react";
import { Button, Table } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import array from "./array";
import { Link, useNavigate } from "react-router-dom";

function Home() {
	let history = useNavigate();

	const [sortedArray, setArray] = useState([]);
	//sort the array once before rendering the component using the use effect hook without any dependencies
	useEffect(() => {
		const sorted = array.sort(function(a, b){
			let x = a.Name.toLowerCase();
			let y = b.Name.toLowerCase();
			if (x < y) {return -1;}
			if (x > y) {return 1;}
			return 0;
		  });
		  setArray(sorted);
	}, []);

	// You may skip this part if you're
	// using react-context api or redux
	function setID(id, name, age) {
		localStorage.setItem("id", id);
		localStorage.setItem("Name", name);
		localStorage.setItem("Age", age);
	}

	// Deleted function - functionality
	// for deleting the entry
	function deleted(id) {
		let index = array
			.map(function (e) {
				return e.id;
			})
			.indexOf(id);

		// deleting the entry with index
		array.splice(index, 1);

		// We need to re-render the page for getting
		// the results so redirect to same page.
		history("/");
	}


	return (
		<div style={{ margin: "5rem" }}>
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Name</th>
						<th>Age</th>
					</tr>
				</thead>
				<tbody>
					{/* Mapping though every element 
						in the array and showing the 
						data in the form of table */}
					{sortedArray.map((item) => {
						return (
							<tr>
								<Link to={`/person/${item.id}`}> <td>{item.Name}</td> </Link>
								{/* getting theid,name, and 
									age for storing these
									value in the jsx with 
									onclick event */}
								<td>
									<Link to={`/edit`}>
										<Button
											onClick={() =>
												setID(
													item.id,
													item.Name,
													item.Age
												)
											}
											variant="info"
										>
											Update
										</Button>
									</Link>
								</td>

								{/* Using thr deleted function passing
									the id of an entry */}
								<td>
									<Button
										onClick={() =>
											deleted(item.id)
										}
										variant="danger"
									>
										Delete
									</Button>
								</td>

								<td>
									<Link to ={"/chart"}>
										View Chart
									</Link>
								</td>
							</tr>
						);
					})}
				</tbody>
			</Table>

			{/* Button for redirecting to create page for
				insertion of values */}
			<Link className="d-grid gap-2" to="/create">
				<Button variant="warning" size="lg">
					Create
				</Button>
			</Link>
		</div>
	);
}

export default Home;
