import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import useLocalStorage from "../hooks/useLocalStorage";

function Edit() {
	const [name, setname] = useState("");
	const [age, setage] = useState("");
	const [salary, setSalary] = useState("");
	const [password, setPassword] = useState("");
	const [id, setid] = useState("");

	let history = useNavigate();

	const handelSubmit = (e) => {
		e.preventDefault();
		useFetch.updateUser(e, id, name, age, salary, password, setname, setage, setSalary, setPassword, history);
	};

	useLocalStorage.getUserDetails(setname, setage, setSalary, setid);

	return (
		<div>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }} onSubmit={handelSubmit}>
				<Form.Group className="mb-3" controlId="formBasicEmail">
					<Form.Control value={name} onChange={(e) => setname(e.target.value)} type="text" placeholder="Enter Name" />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicAge">
					<Form.Control value={age} onChange={(e) => setage(e.target.value)} type="number" placeholder="Age" />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicSalary">
					<Form.Control value={salary} onChange={(e) => setSalary(e.target.value)} type="number" placeholder="Salary" />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicPassword">
					<Form.Control value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" />
				</Form.Group>

				<Button type="submit" variant="primary" size="lg">
					Update
				</Button>

				<Link className="d-grid gap-2" to="/home">
					<Button variant="warning" size="lg">
						Home
					</Button>
				</Link>
			</Form>
		</div>
	);
}

export default Edit;
