import React, { useEffect, useState, useContext } from "react";
import { Button, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Context } from "../App";

function Home() {
  const [array, setArray] = useContext(Context);
  const [_, setServerState] = useContext(Context);
  const [currentPage, setCurrentPage] = useState(1);

  const recPerPage = 5;
  const lastIndex = currentPage * recPerPage;
  const firstIndex = lastIndex - recPerPage;
  const records = array.slice(firstIndex, lastIndex);
  const nPage = Math.ceil(array.length / recPerPage);
  const numbers = [...Array(nPage + 1).keys()].slice(1);

  const handlePrev = () =>
    currentPage !== 1 && setCurrentPage(currentPage - 1);
  const handlePChange = (n) => setCurrentPage(n);
  const handleNext = () =>
    currentPage !== nPage && setCurrentPage(currentPage + 1);

  useEffect(() => {
    fetch("http://localhost:5000/api")
      .then((response) => response.json())
      .then((data) => {
        // Sort the data before setting it
        const sorted = data.sort(function (a, b) {
          let x = a.Name.toLowerCase();
          let y = b.Name.toLowerCase();
          if (x < y) {
            return -1;
          }
          if (x > y) {
            return 1;
          }
          return 0;
        });
        setArray(sorted);
      })
      .catch(() => setServerState(false));
  }, []);

  function setID(id, name, age, salary) {
    localStorage.setItem("id", id);
    localStorage.setItem("Name", name);
    localStorage.setItem("Age", age);
    localStorage.setItem("Salary", salary);
  }

  function deleted(id) {
    fetch(`http://localhost:5000/delete/${id}`, {
      method: "DELETE",
    }).then(window.location.reload());
  }

  return !array ? (
    <div>Array loading...</div>
  ) : (
    <div style={{ margin: "5rem" }}>
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>Name</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {records.map((item) => {
            return (
              <tr key={item.id}>
                <Link to={`/person/${item.id}`}>
                  {" "}
                  <td>{item.Name}</td>{" "}
                </Link>
                <td>
                  <Link to={`/edit`}>
                    <Button
                      onClick={() =>
                        setID(item.id, item.Name, item.Age, item.Salary)
                      }
                      variant="info"
                    >
                      Update
                    </Button>
                  </Link>
                </td>

                {/* Using the deleted function passing
                  the id of an entry */}
                <td>
                  <Button onClick={() => deleted(item.id)} variant="danger">
                    Delete
                  </Button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>

      <nav>
        <ul className="pagination">
          <li className="page-item">
            <a href="#" className="page-link" onClick={handlePrev}>
              Prev
            </a>
          </li>
          {numbers.map((n, i) => (
            <li
              className={`page-item ${currentPage === n ? "active" : ""}`}
              key={i}
            >
              <a
                href="#"
                className="page-link"
                onClick={() => handlePChange(n)}
              >
                {n}
              </a>
            </li>
          ))}
          <li className="page-item">
            <a href="#" className="page-link" onClick={handleNext}>
              Next
            </a>
          </li>
        </ul>
      </nav>

      <Link className="d-grid gap-2" to="/create">
        <Button variant="warning" size="lg">
          Create
        </Button>
      </Link>

      <Link to={"/chart"}>
        <Button variant="contained">View Chart</Button>
      </Link>
    </div>
  );
}

export default Home;
