import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
	const [isAuthenticated, setIsAuthenticated] = useState(false);
	const [person, setPerson] = useState(null);

	useEffect(() => {
		const token = localStorage.getItem("authToken");
		if (token) {
			// Here you can also add a token validation logic if needed
			setIsAuthenticated(true);
			// Assuming `person` information is also stored or can be derived from the token
			setPerson({ name: localStorage.getItem("personName") });
		}
	}, []);

	const login = (newPerson, token) => {
		localStorage.setItem("authToken", token);
		localStorage.setItem("personName", newPerson);
		setIsAuthenticated(true);
		setPerson(newPerson);
	};

	const logout = () => {
		localStorage.removeItem("authToken");
		localStorage.removeItem("personName");

		setIsAuthenticated(false);
		setPerson(null);
	};

	return <AuthContext.Provider value={{ isAuthenticated, person, login, logout }}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
