import React, { useContext, useState } from "react";
import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import useFetch from "../hooks/useFetch";
import { AuthTokenContext } from "../App";

function Authenticate() {
	const [authToken, setAuthToken] = useContext(AuthTokenContext);
	const [name, setname] = useState("");
	const [age, setage] = useState("");
	const [salary, setsalary] = useState("");
	const [password, setPassword] = useState("");
	const [role, setRole] = useState("");

	let history = useNavigate();
	//Function for making a new post request
	const handelSubmit = (e) => useFetch.createPerson(e, name, age, salary, password, setname, setage, setsalary, setPassword, setAuthToken, history);

	return (
		<div>
			<Form className="d-grid gap-2" style={{ margin: "5rem" }}>
				<Form.Group className="mb-3" controlId="formBasicName">
					<Form.Control onChange={(e) => setname(e.target.value)} type="text" placeholder="Enter Name" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicAge">
					<Form.Control onChange={(e) => setage(e.target.value)} type="number" placeholder="Age" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicSalary">
					<Form.Control onChange={(e) => setsalary(e.target.value)} type="number" placeholder="Salary" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicPassword">
					<Form.Control onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" required />
				</Form.Group>

				<Form.Group className="mb-3" controlId="formBasicRole">
					<Form.Control onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" required />
				</Form.Group>

				<Button onClick={(e) => handelSubmit(e)} variant="primary" type="submit">
					Submit
				</Button>

				<Link className="d-grid gap-2" to="/">
					<Button variant="info" size="lg">
						Log in
					</Button>
				</Link>
			</Form>
		</div>
	);
}

export default Authenticate;
