import React, { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
} from "react-router-dom";
import "./App.css";
import Create from "./components/Create";
import Edit from "./components/Edit";
import Home from "./components/Home";
import Details from "./components/Details";
import MyChart from "./components/Chart";

function App() {
  const [arr, setArr] = useState(null);
  const [serverState, setServerState] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/api")
      .then((response) => response.json())
      .then((data) => {
        setArr(data);
      });
  }, []);

  useEffect(() => {

    const checkServerHealth = () => {
      fetch("http://localhost:5000/api/serverhealth")
        .then(() => setServerState(true))
        .catch(() => setServerState(false))
    }

    checkServerHealth();
    const intervalId = setInterval(checkServerHealth, 1000);
    return () => clearInterval(intervalId);
  }, [])

  if (!arr) {
    return <div>Loading...</div>;
  }

  if (!serverState)
    return <div>400 Server down</div>
  else {
    return (
      <div>
        <Router>
          <Routes>
            <Route path="/" element={<Home array={arr} />} />
            <Route path="/create" element={<Create setArr={setArr} />} />
            <Route path="/edit" element={<Edit />} />
            <Route path="/person/:personId" element={<Details />} />
            <Route path="/chart" element={<MyChart array={arr} />} />
          </Routes>
        </Router>
      </div>
    );
  }
}

export default App;
