import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../../Auth/AuthProvider";

function Register() {
	const { login } = useAuth();
	const [name, setName] = useState("");
	const [age, setAge] = useState("");
	const [salary, setSalary] = useState("");
	const [password, setPassword] = useState("");

	const register = async (e) => {
		e.preventDefault();

		if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
			alert("Invalid input");
			setName("");
			setAge("");
			setSalary("");
			setPassword("");
			return;
		}

		const newUser = { name, age, salary, password };
		console.log("Registering user:", newUser); // Debug log

		try {
			const response = await axios.post("http://localhost:5000/auth", newUser, {
				headers: { "Content-Type": "application/json" },
			});
			console.log("Response:", response);

			const { accessToken } = response.data;
			console.log("Access token:", accessToken); // Debug log

			login(name);
			localStorage.setItem("authToken", accessToken);
			// Navigate to home page or perform other actions
		} catch (error) {
			console.error("Registration error:", error.response?.data || error.message);
			alert(error.response?.data.error || "Registration failed. Please check your input.");
		}
	};

	return (
		<form onSubmit={register}>
			<input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
			<input type="number" placeholder="Age" value={age} onChange={(e) => setAge(e.target.value)} />
			<input type="number" placeholder="Salary" value={salary} onChange={(e) => setSalary(e.target.value)} />
			<input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
			<button type="submit">Register</button>
		</form>
	);
}

export default Register;
