import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import "../styles/Create.css";

function Create() {
  const [name, setname] = useState("");
  const [age, setage] = useState("");
  const [salary, setsalary] = useState("");

  let history = useNavigate();

  const handelSubmit = (e) => {
    e.preventDefault();

    if (name === "" || age === "" || salary === "") {
      alert("invalid input");
      return;
    }
    const newPerson = { Name: name, Age: age, Salary: salary };

    fetch("http://localhost:5000/api", {
      method: 'POST',
      headers: {"Content-Type": "application/json" },
      body: JSON.stringify({ user: newPerson })
    }).then(() => window.location.reload());

    history("/");
  };

  return (
    <div className="create-form-container">
      <Form>
        <Form.Group className="create-form-input" controlId="formBasicName">
          <Form.Control
            onChange={(e) => setname(e.target.value)}
            type="text"
            placeholder="Enter Name"
            required
          />
        </Form.Group>

        <Form.Group className="create-form-input" controlId="formBasicAge">
          <Form.Control
            onChange={(e) => setage(e.target.value)}
            type="number"
            placeholder="Age"
            required
          />
        </Form.Group>

        <Form.Group className="create-form-input" controlId="formBasicSalary">
          <Form.Control
            onChange={(e) => setsalary(e.target.value)}
            type="number"
            placeholder="Salary"
            required
          />
        </Form.Group>

        <Button
          className="create-form-button"
          onClick={(e) => handelSubmit(e)}
          variant="primary"
          type="submit"
        >
          Submit
        </Button>

        <Link className="create-form-button" to="/">
          <Button 
		  variant="info"
		   size="lg">
            Home
          </Button>
        </Link>
      </Form>
    </div>
  );
}

export default Create;
