import { useEffect } from "react";
import axios from "axios";

function getOneUser(personId, setPerson, setPetArr) {
	const token = localStorage.getItem("authToken");

	useEffect(() => {
		fetch(`http://localhost:5000/api/${personId}`, {
			headers: { Authorization: `Bearer ${token}` },
		})
			.then((response) => response.json())
			.then((data) => {
				setPerson(data.user);
				setPetArr(data.pets);
			})
			.catch((err) => console.log(err));
	}, []);
}

function deletePet(aid) {
	const token = localStorage.getItem("authToken");

	fetch(`http://localhost:5000/api/pet/${aid}`, {
		method: "DELETE",
		headers: {
			Authorization: `Bearer ${token}`,
			"Content-Type": "application/json",
		},
	})
		.then((res) => {
			if (!res.ok) {
				return res.json().then((error) => {
					throw new Error(error.message);
				});
			}
			alert("Pet deleted successfully");
			window.location.reload();
		})
		.catch((err) => {
			alert(err.message);
		});
}

function createPerson(e, name, age, salary, password, setname, setage, setsalary, setPassword, setAuthToken, history) {
	e.preventDefault();

	if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
		alert("invalid input");
		setname("");
		setage("");
		setsalary("");
		setPassword("");
		window.location.reload();
		return;
	}

	const newUser = { name: name, age: age, salary: salary, password: password };
	fetch("http://localhost:5000/auth", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(newUser),
	})
		.then(async (res) => res.json())
		.then((data) => {
			setAuthToken(data.accessToken);
			localStorage.setItem("authToken", data.accessToken);
			history("/home");
			window.location.reload();
		})
		.catch((err) => console.log(err));

	history("/home");
}

function getPetDetails(aid, setPet) {
	const token = localStorage.getItem("authToken");
	useEffect(() => {
		fetch(`http://localhost:5000/api/pet/${aid}`, {
			headers: {
				Authorization: `Bearer ${token}`,
			},
		})
			.then((res) => res.json())
			.then((data) => {
				setPet(data[0]);
			})
			.catch((err) => console.log(err));
	}, []);
}

function createPet(e, uid, name, history) {
	e.preventDefault();
	const token = localStorage.getItem("authToken");

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const newPet = { pet: { uid: uid, Name: name } };
	fetch("http://localhost:5000/api/pet", {
		method: "POST",
		headers: {
			Authorization: `Bearer ${token}`,
			"Content-Type": "application/json",
		},
		body: JSON.stringify(newPet),
	})
		.then((res) => {
			if (!res.ok) {
				return res.json().then((error) => {
					throw new Error(error.message);
				});
			}
			alert("Pet added successfully!");
		})
		.catch((err) => {
			alert(err.message);
			history("/home");
		});

	history(`/person/${uid}`);
}

function editPet(e, aid, uid, name, history) {
	e.preventDefault();
	const token = localStorage.getItem("authToken");

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const updatedPet = {
		updatedPet: { uid: parseInt(uid), Name: name },
	};

	axios
		.put(`http://localhost:5000/api/pet/${aid}`, updatedPet, {
			headers: {
				Authorization: `Bearer ${token}`,
				"Content-Type": "application/json",
			},
		})
		.then((res) => {
			alert(res.data.message);
			history(`/person/${uid}`);
		})
		.catch((err) => {
			if (err.response) {
				alert(err.response.data.message);
			} else {
				alert("An unexpected error occurred");
			}
			history("/home");
		});
}

export default { getOneUser, deletePet, createPerson, getPetDetails, createPet, editPet };
