import { useEffect, useContext } from "react";
import axios from "axios";

function getAllUsers(setArray, setServerState) {
	useEffect(() => {
		const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJuYW1lIjoiWmV1IiwicGFzc3dvcmQiOiJwYXJvbGExMjMiLCJpYXQiOjE3MTU0MjgzNDYsImV4cCI6MTcxNTQzMTk0Nn0.frj0eey8dDzF9R1_Ph8ixn-MfdMAfHD2whUca_Rsuec";

		fetch("http://localhost:5000/api", {
			headers: { Authorization: `Bearer ${token}` },
		})
			.then((response) => response.json())
			.then((data) => {
				const sorted = data.sort((a, b) => {
					let x = a.Name.toLowerCase();
					let y = b.Name.toLowerCase();
					return x < y ? -1 : x > y ? 1 : 0;
				});
				setArray(sorted);
			})
			.catch(() => setServerState(false));
	}, []);
}

function getOneUser(personId, setPerson, setPetArr) {
	useEffect(() => {
		fetch(`http://localhost:5000/api/${personId}`)
			.then((response) => response.json())
			.then((data) => {
				setPerson(data.user);
				setPetArr(data.pets);
			})
			.catch((err) => console.log(err));
	}, []);
}

function deleted(id) {
	fetch(`http://localhost:5000/delete/${id}`, {
		method: "DELETE",
	})
		.then((res) => {
			if (res.ok) window.location.reload();
			else if (res.status === 402)
				return response.text().then((message) => {
					alert(message);
				});
		})
		.catch((err) => {
			alert("Cannot delete user! He has pets");
			return;
		});
}

function deletePet(aid) {
	fetch(`http://localhost:5000/api/pet/${aid}`, {
		method: "DELETE",
	}).then(window.location.reload());
}

function createPerson(e, name, age, salary, setname, setage, setsalary, history) {
	e.preventDefault();

	if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
		alert("invalid input");
		setname("");
		setage("");
		setsalary("");
		window.location.reload();
		return;
	}

	const newUser = { user: { Name: name, Age: age, Salary: salary } };
	fetch("http://localhost:5000/api", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(newUser),
	}).catch((err) => console.log(err));

	history("/");
}

function updateUser(e, id, name, age, salary, setname, setage, setSalary, history) {
	e.preventDefault();
	if (name === "" || age === "" || salary === "" || parseInt(age) < 0 || parseInt(salary) < 0) {
		alert("invalid input");
		setname("");
		setage("");
		setSalary("");
		window.location.reload();
		return;
	}

	const newData = {
		updatedUser: {
			Name: name,
			Age: parseInt(age),
			Salary: parseInt(salary),
		},
	};

	axios.put(`http://localhost:5000/api/${id}`, newData).catch((err) => console.log(err));

	history("/");
}

function getPetDetails(aid, setPet) {
	useEffect(() => {
		fetch(`http://localhost:5000/api/pet/${aid}`)
			.then((res) => res.json())
			.then((data) => {
				setPet(data[0]);
			})
			.catch((err) => console.log(err));
	}, []);
}

function createPet(e, uid, name, history) {
	e.preventDefault();

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const newPet = { pet: { uid: uid, Name: name } };
	fetch("http://localhost:5000/api/pet", {
		method: "POST",
		headers: { "Content-Type": "application/json" },
		body: JSON.stringify(newPet),
	})
		.then((res) => {
			if (!res.ok) throw new Error();
		})
		.catch((err) => {
			history("/");
			alert("User with that ID doesn't exist... ");
		});

	history(`/person/${uid}`);
}

function editPet(e, aid, uid, name, history) {
	e.preventDefault();

	if (uid === "" || name === "" || parseInt(uid) < 0) {
		alert("Invalid input");
		window.location.reload();
		return;
	}

	const updatedPet = {
		updatedPet: { uid: parseInt(uid), Name: name },
	};
	axios.put(`http://localhost:5000/api/pet/${aid}`, updatedPet).catch((err) => {
		history("/");
		alert("User with that ID doesn't exist... ");
	});

	history(`/person/${uid}`);
}

export default { getAllUsers, getOneUser, deleted, deletePet, createPerson, updateUser, getPetDetails, createPet, editPet };
