import { Chart } from 'chart.js/auto';
import {Bar} from 'react-chartjs-2';    
// import array from './array';
import { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';


const MyChart = ({array}) => {
    const [ages, setAges] = useState([]);
    const [salaries, setSalaries] = useState([]);
    const a = array.map((item) => item.Age).sort();
    const s = array.map((item) => item.Salary);
    for(let i = 1; i < 10;i++)
    {
        const agesCount = a.filter(Age => Age <= 10*i && Age>=10*(i-1)).length;
        console.log(agesCount)
    }

    useEffect(() => {
        setAges(a);
        setSalaries(s);
    }, []);

    return (

        <div style = {{textAlign: 'center'}}>
            <Link to='/'> <Button style={{margin: '10px'}}> Go Back </Button> </Link>
            <Bar 
            data = {{
                labels: salaries,
                datasets : [
                    {
                        label: "Age-salary comparison",
                        data: ages,
                    },
                ],
            }}
            />
        </div>


    );
}

export default MyChart;
