import React, { useEffect, useState, useContext } from "react";
import { Button, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Context } from "../App";
import "../styles/Home.css";

function Home() {
	const [array, setArray] = useContext(Context);
	const [_, setServerState] = useContext(Context);
	const [currentPage, setCurrentPage] = useState(1);

	// Pagination logic
	const recPerPage = 5;
	let lastIndex = currentPage * recPerPage;
	let firstIndex = lastIndex - recPerPage;
	let records = array.slice(firstIndex, lastIndex);
	let nPage = Math.ceil(array.length / recPerPage);
	let numbers = [...Array(nPage + 1).keys()].slice(1);
	let handlePrev = () => currentPage !== 1 && setCurrentPage(currentPage - 1);
	let handlePChange = (n) => setCurrentPage(n);
	let handleNext = () => currentPage !== nPage && setCurrentPage(currentPage + 1);

	// Fetch the data and sort it
	useEffect(() => {
		const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJuYW1lIjoiWmV1IiwicGFzc3dvcmQiOiJwYXJvbGExMjMiLCJpYXQiOjE3MTU0MjEwNzksImV4cCI6MTcxNTQyMTEzOX0.k1cS5X-HhIsQhOVXQCVfbCoucCgPOjlzvdeC5Vj7PVk";

		fetch("http://localhost:5000/api", { headers: { Authorization: `Bearer ${token}` } })
			.then((response) => response.json())
			.then((data) => {
				const sorted = data.sort(function (a, b) {
					let x = a.Name.toLowerCase();
					let y = b.Name.toLowerCase();
					if (x < y) {
						return -1;
					}
					if (x > y) {
						return 1;
					}
					return 0;
				});
				setArray(sorted);
			})
			.catch(() => setServerState(false));
	}, []);

	// Delete entry
	function deleted(id) {
		fetch(`http://localhost:5000/delete/${id}`, {
			method: "DELETE",
		})
			.then((res) => {
				if (res.ok) window.location.reload();
				else if (res.status === 402)
					return response.text().then((message) => {
						alert(message);
					});
			})
			.catch((err) => {
				alert("Cannot delete user! He has pets");
				return;
			});
	}

	function setID(id, name, age, salary) {
		localStorage.setItem("id", id);
		localStorage.setItem("Name", name);
		localStorage.setItem("Age", age);
		localStorage.setItem("Salary", salary);
	}

	return !array ? (
		<div>Array loading...</div>
	) : (
		<div className="home-container">
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Name</th>
						<th>Update</th>
						<th>Delete</th>
					</tr>
				</thead>
				<tbody>
					{records.map((item) => {
						return (
							<tr key={item.id}>
								<Link to={`/person/${item.id}`}>
									<td>{item.Name}</td>
								</Link>
								<td>
									<Link to={`/edit`}>
										<Button onClick={() => setID(item.id, item.Name, item.Age, item.Salary)} variant="info">
											Update
										</Button>
									</Link>
								</td>

								<td>
									<Button onClick={() => deleted(item.id)} variant="danger">
										Delete
									</Button>
								</td>
							</tr>
						);
					})}
				</tbody>
			</Table>

			<nav>
				<ul className="pagination">
					<li className="page-item">
						<a href="#" className="page-link" onClick={handlePrev}>
							Prev
						</a>
					</li>
					{numbers.map((n, i) => (
						<li className={`page-item ${currentPage === n ? "active" : ""}`} key={i}>
							<a href="#" className="page-link" onClick={() => handlePChange(n)}>
								{n}
							</a>
						</li>
					))}
					<li className="page-item">
						<a href="#" className="page-link" onClick={handleNext}>
							Next
						</a>
					</li>
				</ul>
			</nav>

			<Link className="d-grid gap-2" to="/create">
				<Button variant="warning" size="lg">
					Create
				</Button>
			</Link>

			<Link to={"/chart"}>
				<Button variant="contained">View Chart</Button>
			</Link>
		</div>
	);
}

export default Home;
