import React, { useContext, useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Context } from "../App";
import { Button, Table } from "react-bootstrap";

function Details() {
  const [person, setPerson] = useState(null);
  const { personId } = useParams();
  const [petArr, setPetArr] = useContext(Context); //[{aid: 1 uid: 1 name: ...}, {...}, ...]

  //Get person with id personId
  useEffect(() => {
    fetch(`http://localhost:5000/api/${personId}`)
      .then(() => window.location.reload())
      .then((response) => response.json())
      .then((data) => {
        setPerson(data.user);
        setPetArr(data.pets);
      })

      .catch((err) => console.log(err));
  }, []);

  function setPetId(aid, uid, name) {
    localStorage.setItem("aid", aid);
    localStorage.setItem("uid", uid);
    localStorage.setItem("Name", name);
  }

  function deleted(aid) {
    fetch(`http://localhost:5000/api/pet/${aid}`, {
      method: "DELETE",
    }).then(window.location.reload());
  }

  return !person ? (
    <div>Loading...</div>
  ) : (
    <div style={{ padding: "20px" }}>
      <Link to={`/`}>
        <Button>Back</Button>
      </Link>
      <div>
        <h3>Id: {person.id}</h3>
        <h3>Name: {person.Name}</h3>
        <h3>Age: {person.Age}</h3>
        <h3>Salary: {person.Salary}</h3>
      </div>

      <br />
      <br />

      <div>
        <h2>This person's dogs</h2>
        <Table striped bordered hover size="sm">
          <thead>
            <tr>
              <th>Name</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {petArr.map((item) => {
              return (
                <tr key={item.aid}>
                  <Link to={`/pet/${item.aid}`}>
                    <td>{item.Name}</td>
                  </Link>

                  <td>
                    <Link to={`/pet/edit`}>
                      <Button
                        onClick={() => setPetId(item.aid, item.uid, item.Name)}
                        variant="info"
                      >
                        Update
                      </Button>
                    </Link>
                  </td>

                  <td>
                    <Button onClick={() => deleted(item.aid)} variant="danger">
                      Delete
                    </Button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>

        <Link to={`/pet/create`}>
          <Button variant="warning" size="lg">
            Create
          </Button>
        </Link>
      </div>
    </div>
  );
}

export default Details;
