import {Chart as ChartJS} from 'chart.js/auto';
import {Bar, Doughnut, Line} from 'react-chartjs-2';    
import array from './array';
import { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';

const MyChart = () => {
    const [ages, setAges] = useState([]);
    const [salarie, setSalaries] = useState([]);
    const a = array.map((item) => item.Age);
    const s = array.map((item) => item.Salary);

    useEffect(() => {
        setAges(a);
        setSalaries(s);
    }, []);

    return (

        <div>
            <Button onClick={() => alert(ages)}>Clcik</Button>
            <Bar 
            data = {{
                labels: ['A', 'B', 'C'],
                datasets : [
                    {
                        label: "Revenue",
                        data: [200,300,400],
                    },
                ],
            }}
            />
        </div>


    );
}

export default MyChart;
