import React, { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function PetCreate() {
  const [aid, setAid] = useState("");
  const [uid, setUid] = useState("");
  const [name, setName] = useState("");

  let history = useNavigate();

  const handelSubmit = (e) => {
    e.preventDefault();

    if (uid === "" || name === "" || parseInt(uid) < 0) {
      alert("Invalid input");
      return;
    }

    const updatedPet = {
      updatedPet: { uid: parseInt(uid), Name: name },
    };
    axios
      .put(`http://localhost:5000/api/pet/${aid}`, updatedPet)
      .catch((err) => {
        history("/");
        alert("User with that ID doesn't exist... ");
      });

    history(`/person/${uid}`);
  };

  useEffect(() => {
    setAid(localStorage.getItem("aid"));
    setUid(localStorage.getItem("uid"));
    setName(localStorage.getItem("Name"));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <Link to={`/person/${aid}`}>
        <Button>Back</Button>
      </Link>
      <Form className="d-grid gap-2" style={{ margin: "5rem" }}>
        <Form.Group className="mb-3" controlId="formBasicUid">
          <Form.Control
            value={uid}
            onChange={(e) => setUid(e.target.value)}
            type="number"
            placeholder="Enter Uid"
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicName">
          <Form.Control
            value={name}
            onChange={(e) => setName(e.target.value)}
            type="text"
            placeholder="Enter Name"
            required
          />
        </Form.Group>

        <Button
          onClick={(e) => handelSubmit(e)}
          variant="primary"
          type="submit"
        >
          Submit
        </Button>
        <Link className="d-grid gap-2" to="/">
          <Button variant="info" size="lg">
            Home
          </Button>
        </Link>
      </Form>
    </div>
  );
}

export default PetCreate;
