import React, { useEffect, useState, useContext } from "react";
import { Button, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Context } from "../App";
import "../styles/Home.css";
import useFetch from "../hooks/useFetch";
import useLocalStorage from "../hooks/useLocalStorage";
import AUTH_TOKEN from "../data/authentication_token";

function Home() {
	const contextArray = useContext(Context);
	const [array, setArray] = contextArray[0];
	const [serverState, setServerState] = useContext(Context);
	const [currentPage, setCurrentPage] = useState(1);
	const [authToken, setAuthToken] = useState(AUTH_TOKEN);

	// Pagination logic
	const recPerPage = 5;
	let lastIndex = currentPage * recPerPage;
	let firstIndex = lastIndex - recPerPage;
	let records = array.slice(firstIndex, lastIndex);
	let nPage = Math.ceil(array.length / recPerPage);
	let numbers = [...Array(nPage + 1).keys()].slice(1);
	let handlePrev = () => currentPage !== 1 && setCurrentPage(currentPage - 1);
	let handlePChange = (n) => setCurrentPage(n);
	let handleNext = () => currentPage !== nPage && setCurrentPage(currentPage + 1);

	//fetches
	useFetch.getAllUsers(setArray, setServerState, authToken);
	const deleted = (id) => useFetch.deleted(id);
	const setEditValues = (id, name, age, salary) => useLocalStorage.setEditValues(id, name, age, salary);

	return !array ? (
		<div> Array loading...</div>
	) : (
		<div className="home-container">
			{console.log(serverState)}
			<Table striped bordered hover size="sm">
				<thead>
					<tr>
						<th>Name</th>
						<th>Update</th>
						<th>Delete</th>
					</tr>
				</thead>
				<tbody>
					{records.map((item) => {
						return (
							<tr key={item.id}>
								<Link to={`/person/${item.id}`}>
									<td>{item.Name}</td>
								</Link>
								<td>
									<Link to={`/edit`}>
										<Button onClick={() => setEditValues(item.id, item.Name, item.Age, item.Salary)} variant="info">
											Update
										</Button>
									</Link>
								</td>

								<td>
									<Button onClick={() => deleted(item.id)} variant="danger">
										Delete
									</Button>
								</td>
							</tr>
						);
					})}
				</tbody>
			</Table>

			<nav>
				<ul className="pagination">
					<li className="page-item">
						<a href="#" className="page-link" onClick={handlePrev}>
							Prev
						</a>
					</li>
					{numbers.map((n, i) => (
						<li className={`page-item ${currentPage === n ? "active" : ""}`} key={i}>
							<a href="#" className="page-link" onClick={() => handlePChange(n)}>
								{n}
							</a>
						</li>
					))}
					<li className="page-item">
						<a href="#" className="page-link" onClick={handleNext}>
							Next
						</a>
					</li>
				</ul>
			</nav>

			<Link className="d-grid gap-2" to="/create">
				<Button variant="warning" size="lg">
					Create
				</Button>
			</Link>

			<Link to={"/chart"}>
				<Button variant="contained">View Chart</Button>
			</Link>
		</div>
	);
}

export default Home;
