import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
	const [isAuthenticated, setIsAuthenticated] = useState(false);
	const [person, setPerson] = useState(null);
	const [loading, setLoading] = useState(true);

	useEffect(() => {
		const token = localStorage.getItem("authToken");
		const personName = localStorage.getItem("personName");

		if (token) {
			setIsAuthenticated(true);
			setPerson(personName);
		}
		setLoading(false); // Indicate that loading is done
	}, []);

	const login = (newPerson, token) => {
		localStorage.setItem("authToken", token);
		localStorage.setItem("personName", newPerson);
		setIsAuthenticated(true);
		setPerson(newPerson);
	};

	const logout = () => {
		localStorage.removeItem("authToken");
		localStorage.removeItem("personName");

		setIsAuthenticated(false);
		setPerson(null);
	};

	if (loading) {
		return <div>Loading...</div>; // Show a loading indicator while checking auth state
	}

	return <AuthContext.Provider value={{ isAuthenticated, person, login, logout }}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
