// Filename - components/Create.js

import React, { useEffect, useState } from "react";
import { Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
// import array from "./array";
import { Link, json, useNavigate } from "react-router-dom";

function Create({setArr}) {
	// Making usestate for setting and
	// fetching a value in jsx
	const [person, setPerson] = useState(null);
	const [name, setname] = useState("");
	const [age, setage] = useState("");
	const [salary, setsalary] = useState("");

	// Using useNavigation for redirecting to pages
	let history = useNavigate();

	// Function for creating a post/entry
	const handelSubmit = (e) => {
		e.preventDefault(); // Prevent reload

		const generateRandomIntegerId = () => {
			const min = 10000000; // Minimum value for 8-digit integer
			const max = 99999999; // Maximum value for 8-digit integer
			return Math.floor(Math.random() * (max - min + 1)) + min;
		};

		const id = generateRandomIntegerId()

		// Fetching a value from usestate and
		// pushing to javascript object
		let a = name,
			b = age,
			c = salary;
		if (name == "" || age == "" || salary == "") {
			alert("invalid input");
			return;
		}
		const p = {Name:name, Age:age, Salary:salary}

		fetch("http://localhost:5000/api", {
			method: 'POST',
			headers: {"Content-Type": "application/json" },
			body: JSON.stringify({user: p})
		}).then(() => window.location.reload())
		

		// Redirecting to home page after creation done
		history("/");
	};


	return (
		<div>
			<Form
				className="d-grid gap-2"
				style={{ margin: "5rem" }}
			>
				{/* Fetching a value from input textfirld 
					in a setname using usestate*/}
				<Form.Group
					className="mb-3"
					controlId="formBasicName"
				>
					<Form.Control
						onChange={(e) =>
							setname(e.target.value)
						}
						type="text"
						placeholder="Enter Name"
						required
					/>
				</Form.Group>

				{/* Fetching a value from input textfirld in
					a setage using usestate*/}
				<Form.Group
					className="mb-3"
					controlId="formBasicAge"
				>
					<Form.Control
						onChange={(e) =>
							setage(e.target.value)
						}
						type="number"
						placeholder="Age"
						required
					/>
				</Form.Group>
				
				<Form.Group
					className="mb-3"
					controlId="formBasicAge"
				>
					<Form.Control
						onChange={(e) =>
							setsalary(e.target.value)
						}
						type="number"
						placeholder="Salary"
						required
					/>
				</Form.Group>

				{/* handing a onclick event in button for
					firing a function */}
				<Button
					onClick={(e) => handelSubmit(e)}
					variant="primary"
					type="submit"
				>
					Submit
				</Button>

				{/* Redirecting back to home page */}
				<Link className="d-grid gap-2" to="/">
					<Button variant="info" size="lg">
						Home
					</Button>
				</Link>
			</Form>
		</div>
	);
}

export default Create;
