import { useEffect, useContext } from "react";

function useFetchData(setArray, setServerState) {
	useEffect(() => {
		const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJuYW1lIjoiWmV1IiwicGFzc3dvcmQiOiJwYXJvbGExMjMiLCJpYXQiOjE3MTU0MjUxODQsImV4cCI6MTcxNTQyNTI0NH0.v54Zo4vHE_c9PCx-KMBX05SpIbO4rxI0jk3QOJU5QCU";

		fetch("http://localhost:5000/api", {
			headers: { Authorization: `Bearer ${token}` },
		})
			.then((response) => response.json())
			.then((data) => {
				const sorted = data.sort((a, b) => {
					let x = a.Name.toLowerCase();
					let y = b.Name.toLowerCase();
					return x < y ? -1 : x > y ? 1 : 0;
				});
				setArray(sorted);
			})
			.catch(() => setServerState(false));
	}, []);
}

export default useFetchData;
